mapper.select:
SELECT SEQ, ID, REF, STEP, DEPTH, READCOUNT, TITLE, CONTENT, RDATE, TYPE, DEL, USERID FROM (SELECT ROW_NUMBER()OVER(ORDER BY REF DESC, STEP ASC) AS RNUM,SEQ, ID, REF, STEP, DEPTH, READCOUNT, TITLE, CONTENT, RDATE, TYPE, DEL, USERID FROM CS_BBS WHERE 1=1
AND TITLE LIKE '%'||#{search}||'%'
AND CONTENT LIKE '%'||#{search}||'%'
AND ID=#{search}
ORDER BY REF DESC, STEP ASC) WHERE RNUM BETWEEN ${start} AND ${end}

SELECT SEQ, ID, REF, STEP, DEPTH, TITLE, CONTENT, RDATE, TYPE, DEL, READCOUNT, USERID
FROM CS_BBS
WHERE SEQ=#{seq}

mapper.update:
UPDATE CS_BBS
SET READCOUNT=READCOUNT+1
WHERE SEQ=#{seq}

UPDATE CS_BBS
SET DEL=1
WHERE SEQ=#{seq}

UPDATE CS_BBS SET
TITLE=#{title}, CONTENT=#{content}, RDATE=SYSDATE
WHERE SEQ=#{seq}
