mapper.select:
SELECT NVL(COUNT(*), 0)
FROM MEMBER
WHERE ID=#{ id , jdbcType=VARCHAR}

SELECT NVL(COUNT(*), 0)
FROM MEMBER
WHERE NICKNAME=#{ nickname }

SELECT SEQ, ID, PWD, NAME, NICKNAME, BIRTH, GENDER, PHONE, ADDRESS1, EMAIL, JOINDATE, AUTH
FROM MEMBER
WHERE NAME=#{name} AND PHONE=#{phone} AND EMAIL=#{email}

SELECT SEQ, ID, PWD, NAME, NICKNAME, BIRTH, GENDER, PHONE, ADDRESS1, EMAIL, JOINDATE, AUTH
FROM MEMBER
WHERE ID = #{id}

SELECT COUNT(*) 
FROM MEMBER 
WHERE ID = #{id}

SELECT COUNT(*) 
FROM MEMBER
WHERE NICKNAME = #{nickname}

SELECT COUNT(*)
FROM MEMBER 
WHERE EMAIL = #{email}

SELECT SEQ, ID, PWD, NAME, NICKNAME, BIRTH, GENDER, PHONE, ADDRESS1, EMAIL, JOINDATE, SNS_TYPE, AUTH
FROM MEMBER
WHERE ID = #{id}
