mapper.select:
SELECT SEQ, ID, PWD, NAME, NICKNAME, BIRTH, GENDER, PHONE, EMAIL, ADDRESS1, AUTH, JOINDATE FROM (SELECT ROW_NUMBER()OVER(ORDER BY SEQ DESC) AS RNUM, SEQ, ID, PWD, NAME, NICKNAME, BIRTH, GENDER, PHONE, EMAIL, ADDRESS1, AUTH, JOINDATE FROM MEMBER WHERE 1=1
AND NAME LIKE '%'||#{search}||'%'
AND NICKNAME LIKE '%'||#{search}||'%'
AND ID=#{search}
ORDER BY SEQ ASC) WHERE RNUM BETWEEN ${start} AND ${end}

SELECT SEQ, ID, RECIPETITLE, BESTIMG, NEWBESTIMG, RECINTRODUCE, RECCATEGORY, RECPERSON, RECTIME, RECLEVEL, INGREDIENT, READCOUNT, LIKED, RECCONTENT, RECCONTENT_B, RECCONTENT_C, RECCONTENT_D, RECCONTENT_E, RECCONTENT_F, RECIMG, NEWRECIMG, RECIMG_B, NEWRECIMG_B, RECIMG_C, NEWRECIMG_C, RECIMG_D, NEWRECIMG_D, RECIMG_E, NEWRECIMG_E, RECIMG_F, NEWRECIMG_F, DEL FROM (SELECT ROW_NUMBER()OVER(ORDER BY SEQ DESC) AS RNUM, SEQ, ID, RECIPETITLE, BESTIMG, NEWBESTIMG, RECINTRODUCE, RECCATEGORY, RECPERSON, RECTIME, RECLEVEL, INGREDIENT, READCOUNT, LIKED, RECCONTENT, RECCONTENT_B, RECCONTENT_C, RECCONTENT_D, RECCONTENT_E, RECCONTENT_F, RECIMG, NEWRECIMG, RECIMG_B, NEWRECIMG_B, RECIMG_C, NEWRECIMG_C, RECIMG_D, NEWRECIMG_D, RECIMG_E, NEWRECIMG_E, RECIMG_F, NEWRECIMG_F, DEL FROM RECIPE WHERE 1=1
AND RECIPETITLE LIKE '%'||#{search}||'%'
AND RECCATEGORY LIKE '%'||#{search}||'%'
AND ID=#{search}
ORDER BY SEQ ASC) WHERE RNUM BETWEEN ${start} AND ${end}

SELECT SEQ, NICKNAME, TITLE, LOCATION, KINDS, READCOUNT, MEMBERCOUNT, CONTENT, MEMBERNUM, RDATE, EDATE, CDATE1, CDATE2, CDAY, FILENAME, NEWFILENAME, DEL FROM (SELECT ROW_NUMBER()OVER(ORDER BY SEQ DESC) AS RNUM, SEQ, NICKNAME, TITLE, LOCATION, KINDS, READCOUNT, MEMBERCOUNT, CONTENT, MEMBERNUM, RDATE, EDATE, CDATE1, CDATE2, CDAY, FILENAME, NEWFILENAME, DEL FROM COOKCLASS WHERE 1=1
AND TITLE LIKE '%'||#{search}||'%'
AND KINDS LIKE '%'||#{search}||'%'
AND NICKNAME=#{search}
ORDER BY SEQ DESC) WHERE RNUM BETWEEN ${start} AND ${end}

SELECT SEQ, CANUM, SUBCA, ITEMNUM, TITLE, SUBTITLE, PRICE, GRAM, COUNTRY, EXPIRATION, COUNT, CONTENT, MAINPT, NEWMAINPT, RVCOUNT, RDATE FROM (SELECT ROW_NUMBER()OVER(ORDER BY SEQ DESC) AS RNUM, SEQ, CANUM, SUBCA, ITEMNUM, TITLE, SUBTITLE, PRICE, GRAM, COUNTRY, EXPIRATION, COUNT, CONTENT, MAINPT, NEWMAINPT, RVCOUNT, RDATE FROM MARKET WHERE 1=1
AND TITLE LIKE '%'||#{search}||'%'
AND SUBTITLE=#{search}
ORDER BY SEQ ASC) WHERE RNUM BETWEEN ${start} AND ${end}

SELECT NVL(COUNT(*), 0) FROM MARKET WHERE 1=1
AND TITLE LIKE '%'||#{search}||'%'
AND SUBTITLE=#{search}

SELECT SEQ, ID, ORDERNUM, ITEMNUM, TITLE, MAINPT, PRICE, COUNT, CANUM, PDATE FROM (SELECT ROW_NUMBER()OVER(ORDER BY SEQ DESC) AS RNUM, SEQ, ID, ORDERNUM, ITEMNUM, TITLE, MAINPT, PRICE, COUNT, CANUM, PDATE FROM INCOME WHERE 1=1
AND TITLE LIKE '%'||#{search}||'%'
AND ID=#{search}
ORDER BY SEQ ASC) WHERE RNUM BETWEEN ${start} AND ${end}

SELECT NVL(COUNT(*), 0) FROM INCOME WHERE 1=1
AND TITLE LIKE '%'||#{search}||'%'
AND ID=#{search}
